
class AirthOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 7;
		int num2 = 3;
		// 결합방향 : ->
		System.out.println("num1 + num2 = " + (num1 + num2));		
		System.out.println("num1 - num2 = " + (num1 - num2));
		System.out.println("num1 / num2 = " + (num1 / num2));
		System.out.println("num1 * num2 = " + (num1 * num2));
		System.out.println("num1 % num2 = " + (num1 % num2));
		
		num1 += 1;
		// num1 = num1 + 1;
		System.out.println(num1);
	}

}
